sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    var reserveType = api.data.look_up_record_2.result.name.displayValue.substring(0, 4);
    var href = '#';

    switch (reserveType) {
        case "Meet":
            href = 'x/1540355/reservation-system/meeting-room-list-page';
            break;
        case "Vehi":
            href = 'x/1540355/reservation-system/vehicle-list-page';
            break;
        case "Acco":
            href = 'x/1540355/reservation-system/accommodation-list-page';
            break;
        case "Rest":
            href = 'x/1540355/reservation-system/restaurant-list-page';
            break;
        default:
            // code block
    }
    return [{
            "label": helpers.translateSync("Home"),
            "icon": "home-outline",
            "hideLabel": false,
            "href": "x/1540355/reservation-system/landing"
        },
        {
            "label": "List",
            "hideLabel": false,
            "href": href
        },
        {
            "label": api.data.look_up_record_2.result.name.displayValue
        }

    ];
}
